import java.util.Scanner;public class Main {    public static void main(String[] args){        Scanner scanner = new Scanner(System.in);        ParseBook book = new ParseBook();        ElasticMetods ES = new ElasticMetods();        while(true) {            System.out.println("Меню:");            System.out.println("Нажмите 1 чтобы заполнить индекс");            System.out.println("Нажмите 2 для поиска");            System.out.println("Нажмите 3 для выхода");            int a = Integer.valueOf(scanner.nextLine());            switch (a) {                case 1:                    ES.PushWordsInElastic(book.GetWordsPdf("src/Stone.pdf"));                    break;                case 2:                    System.out.println("Введите слово: ");                    String searchWord = scanner.nextLine();                    ES.SearchWord(searchWord);                    break;                case 3:                    System.exit(0);            }        }    }}